package android.batch1;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.DefaultItemAnimator;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import androidx.recyclerview.widget.StaggeredGridLayoutManager;

import android.os.Bundle;

import java.util.ArrayList;

public class MyntraCategoryActivity extends AppCompatActivity {

    RecyclerView recyclerView;
    String[] catNameArray = {"Category 1", "Category 2", "Category 3", "Category 4", "Category 5"};
    String[] catOffArray = {"50 - 60% OFF", "50 - 80% OFF", "30 - 50% OFF", "30 - 40% OFF", "20 - 30% OFF"};
    int[] catImageArray = {R.drawable.cat1, R.drawable.cat2, R.drawable.cat3, R.drawable.cat4, R.drawable.cat5};

    String[] catImageStringArray = {
            "data:image/jpeg;base64,",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRGG94sVMCpyUa4Cf79_mQKj0OzcBmnxxJ0TA&usqp=CAU",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQqYIBhUOF9gJ9FFctt1isUJgEyqZF1wzSwhg&usqp=CAU",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR_dNLXoDDRCkGPnBCGHWez3CxJFRKr0eGlBA&usqp=CAU",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSuyIicfO7eOHpQgZPjimqT9KmnttPEEoU7iw&usqp=CAU"
    };
    ArrayList<MyntraList> arrayList;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_myntra_category);
        recyclerView = findViewById(R.id.myntra_category_recycler);

        //recyclerView.setLayoutManager(new LinearLayoutManager(MyntraCategoryActivity.this));

        //recyclerView.setLayoutManager(new StaggeredGridLayoutManager(2, StaggeredGridLayoutManager.VERTICAL));

        recyclerView.setLayoutManager(new StaggeredGridLayoutManager(2, StaggeredGridLayoutManager.HORIZONTAL));

        recyclerView.setItemAnimator(new DefaultItemAnimator());

        arrayList = new ArrayList<>();
        for (int i = 0; i < catNameArray.length; i++) {
            MyntraList list = new MyntraList();
            list.setName(catNameArray[i]);
            list.setOffer(catOffArray[i]);
            list.setImage(catImageArray[i]);
            list.setImageString(catImageStringArray[i]);
            arrayList.add(list);
        }
        MyntraCategoryAdapter adapter = new MyntraCategoryAdapter(MyntraCategoryActivity.this, arrayList);
        recyclerView.setAdapter(adapter);
    }
}